import React from 'react'
import { useState } from 'react'
export const EditTodoForm = ({EditTodo,myTodo}) => {
    const mytodo=myTodo;
    const[text,setText]=useState(mytodo.task);
    const handleSubmit=(e)=>{
    e.preventDefault();
    if (text.trim().lenght >0) {
      EditTodo(text,mytodo.id);
    setText("");
      alert("Please enter a value in the input field.");
      return false; // Prevent form submission
    }
    else{
      alert("Please enter a value in the input field.");
      return false;
    }
    }
  return (
   <form action="" onSubmit={handleSubmit}>
    <input type="text" value={text} placeholder='Update here' onChange={(e)=>{setText(e.target.value)}}/>
    <button type="submit" className='btn'>Update Item</button>
   </form>
  )
}

export default EditTodoForm
